mapper.select:
select jc as name from TsflkView where jc not like '%' + #{cg} + '%'
and jc not like '%' + #{zx} + '%' and jc not like '%' + #{wh} + '%'
and sjdm <> #{sjdm}

select distinct shum as name from fxsgkview where sxh not like '%' + #{sxh} + '%'
and tsfljc not like '%' + #{tsfljc} + '%' and bmmc <> #{cbzx}
and bmmc <> #{whcb}
